mapper.select:
SELECT p.id,p.from_time AS fromTime,p.to_time AS toTime,p.parts_no AS partsNo,p.parts_name AS partsName,p.instruction,p.group_no AS groupNo,p.pnc FROM car_parts_info p LEFT JOIN car_parts_info_type_detail i ON i.car_parts_info_id=p.id LEFT JOIN car_type_detail d ON i.car_type_detail_id = d.id LEFT JOIN car_type t ON i.car_type_id=t.id where 1=1
and i.car_type_id=#{carTypeId}
and i.car_type_detail_id=#{carTypeDetailId}
and p.group_no like CONCAT('%',#{groupNo},'%')
LIMIT #{key_offset},20

SELECT count(p.id) FROM car_parts_info p LEFT JOIN car_parts_info_type_detail i ON i.car_parts_info_id=p.id LEFT JOIN car_type_detail d ON i.car_type_detail_id = d.id LEFT JOIN car_type t ON i.car_type_id=t.id where 1=1
and i.car_type_id=#{carTypeId}
and i.car_type_detail_id=#{carTypeDetailId}
and p.group_no like CONCAT('%',#{groupNo},'%')
